let csvData="0134134,John Smith,555-567-2341,62 inches\n0134135   ,    June    Lee    ,  5554126347 ,        149 cm\n0134136,       Kim Thomas       , 5324126347, 138cm";function splitIntoRows(e){return e.split(/\r?\n/)}function splitIntoFields(e){return e.split(/\s*,\s*/)}function formatName(e){return e.replace(/\s{2,}/," ")}function findAreaCode(e){return e.match(/(\d{3})-?\d{3}-?\d{4}/)[1]||"No Area Code"}function getHeightInInches(e){if(e.endsWith("inches"))return e;return`${(.39*parseFloat(e)).toFixed(2)} inches`}function processCSV(e){return splitIntoRows(e).map((function(e){let[n,t,o,s]=splitIntoFields(e);return t=formatName(t),o=findAreaCode(o),[n,t,o,getHeightInInches(s)].join(",")})).join("\n")}let processed=processCSV(csvData);console.log(processed);